import logo from './logo.svg';
import './App.css';
import TaqCategoryList from './components/TaqCategoryList';
import { useEffect, useState } from 'react';
import axios from 'axios'; // Import axios for making HTTP requests

function App() {
  const [TaqCategories, setTaqCategories] = useState([]);

  // Function to fetch categories from API
  const getCategories = async () => {
    try {
      const TaqCateResponse = await axios.get("https://667390c36ca902ae11b494ca.mockapi.io/api/taqv/"); 
      setTaqCategories(TaqCateResponse.data);
    } catch (error) {
      console.error('Error fetching categories:', error);
      // Handle error state if needed
    }
  };

  // Fetch categories on component mount
  useEffect(() => {
    getCategories();
  }, []); // Empty dependency array ensures this effect runs once after initial render

  return (
    <div className="container border my-3">
      <h1>Truong Anh Quan - call api</h1>

      {/* Pass TaqCategories as props to TaqCategoryList component */}
      <TaqCategoryList categories={TaqCategories} />
    </div>
  );
}

export default App;
